import React, { useEffect, useState } from 'react';
import { Line } from 'react-chartjs-2';
import socketIOClient from 'socket.io-client';

// endpoint for Socket.IO server
const endpoint = "http://localhost:3000";

function HumidityChart() {
  // we are loading initial chart data from localStorage 
  const [c, setChartData] = useState(() => {
    const s = localStorage.getItem('humidityChartData');
    return s ? JSON.parse(s) : {
      labels: [],
      datasets: [{
        label: 'Humidity (%)',
        data: [],
        fill: false,
        backgroundColor: 'blue',
        borderColor: 'blue',
      }]
    };
  });

  //initialing connection to socket
  useEffect(() => {
    const socket = socketIOClient(endpoint, { transports: ["websocket"] });

    // we listen to MQTT messages for humidity
    socket.on('mqtt_message', (data) => {
      if (data.topic === 'sensors/humidity') {
        try {
          const dataObj = JSON.parse(data.message);
          const hval = parseFloat(dataObj.humidity);
          if (!isNaN(hval)) {
            const cTime = new Date().toLocaleTimeString();
            setChartData(prevData => {
              const newLabels = [...prevData.labels, cTime];
              const newData = [...prevData.datasets[0].data, hval];
              // Keep only the last 10 data points
              if (newLabels.length > 10) {
                newLabels.shift();
                newData.shift();
              }

              // Persist updated chart data in localStorage
              const updatedChartData = {
                labels: newLabels,
                datasets: [{
                  ...prevData.datasets[0],
                  data: newData,
                }]
              };
              localStorage.setItem('humidityChartData', JSON.stringify(updatedChartData));
              return updatedChartData;
            });
          }
        } catch (error) {
          console.error("Error parsing humidity data", error);
        }
      }
    });

    return () => socket.disconnect();
  }, []);

  return (
    <div style={{ width: '100%', maxWidth: '600px' }}>
      <h5>Humidity vs. Time</h5>
      <Line data={c} />
    </div>
  );
}

export default HumidityChart;
