import React from 'react';

function TemperatureWidget({ temperature }) {
  // Clamp temperature between 0 and 100
  const clampedTemp = Math.min(Math.max(temperature, 0), 100);
  const fillHeight = clampedTemp + '%';

  // Determine fill color based on temperature
  let fillColor;
  if (clampedTemp < 33) {
    fillColor = "#00aaff"; // Blue for cold
  } else if (clampedTemp < 66) {
    fillColor = "#00cc66"; // Green for moderate
  } else {
    fillColor = "#ff3300"; // Red for hot
  }

  return (
    <div style={{ textAlign: 'center' }}>
      <h3 style={{ marginBottom: '10px', fontFamily: 'Arial, sans-serif' }}>
        Temperature
      </h3>
      <div style={{
        width: '60px',
        height: '200px',
        border: '2px solid #333',
        borderRadius: '10px',
        backgroundColor: '#eee',
        position: 'relative',
        overflow: 'hidden',
        margin: '0 auto'
      }}>
        <div style={{
          position: 'absolute',
          bottom: 0,
          width: '100%',
          height: fillHeight,
          backgroundColor: fillColor,
          transition: 'height 0.5s ease-in-out, background-color 0.5s ease-in-out'
        }} />
        <div style={{
          position: 'absolute',
          bottom: 0,
          width: '100%',
          textAlign: 'center',
          fontWeight: 'bold',
          color: '#333',
          paddingBottom: '5px'
        }}>
          {temperature}F
        </div>
      </div>
      <div style={{ marginTop: '30px', fontWeight: 'bold' }}>
      </div>
    </div>
  );
}

export default TemperatureWidget;
