import React from 'react';
import './TemperatureWidget.css';

function TemperatureWidget({ temperature }) {
  // Clamp temperature between 0 and 100
  const clampedTemp = Math.min(Math.max(temperature, 0), 100);
  const fillHeight = clampedTemp + '%';

  // Determine fill color based on temperature
  let fillColor;
  if (clampedTemp < 33) {
    fillColor = "#00aaff"; // Blue for cold
  } else if (clampedTemp < 66) {
    fillColor = "#00cc66"; // Green for moderate
  } else {
    fillColor = "#ff3300"; // Red for hot
  }

  return (
    <div className="temperature-widget">
      <h3 className="temperature-title">Temperature</h3>
      <div className="thermometer">
        <div
          className="thermometer-fill"
          style={{
            height: fillHeight,
            backgroundColor: fillColor,
          }}
        />
        <div className="thermometer-label">
          {temperature}F
        </div>
      </div>
    </div>
  );
}

export default TemperatureWidget;
